} else {
  $new_node_path="/mnt/c/Users/Nucl3/Desktop/ServerServeros/node_modules/.pnpm/typeorm@0.3.11_mysql2@2.3.3_cd46b23042e57f10b3014dbbd75edec9/node_modules/typeorm/node_modules:/mnt/c/Users/Nucl3/Desktop/ServerServeros/node_modules/.pnpm/typeorm@0.3.11_mysql2@2.3.3_cd46b23042e57f10b3014dbbd75edec9/node_modules:/mnt/c/Users/Nucl3/Desktop/ServerServeros/node_modules/.pnpm/node_modules"
}
